import React from 'react';
import { VictoryBar, VictoryChart, VictoryAxis, VictoryTooltip, VictoryLine, VictoryGroup } from 'victory';

const AllStudentsGraph = (props) => {
	console.log('props.data', props.data);

	const projectNames = props.data.map((item) => item.project);
	const uniqueprojectNames = projectNames.filter((name, index, names) => {
		// Truc om dubbele namen er uit te filteren
		return names.indexOf(name) === index;
	});
	console.log('uniqueprojectNames', uniqueprojectNames);
	const projectAverages = uniqueprojectNames.map((name) => {
		const projectValues = props.data.filter((item) => item.project === name);
		const difficultyAverage =
			projectValues.reduce((prev, curr) => prev + curr.difficulty, 0) / projectValues.length;
		const enjoyabilityAverage =
			projectValues.reduce((prev, curr) => prev + curr.enjoyability, 0) / projectValues.length;
		return {
			project: name,
			difficultyAverage: difficultyAverage,
			enjoyabilityAverage: enjoyabilityAverage
		};
	});
	// Waarde om mee te geven aan de VictoryBar:
	console.log(projectAverages);

	return (
		<VictoryChart domainPadding={15} width={1500}>
			<VictoryGroup offset={10}>
				<VictoryBar data={props.data} x="project" y="dificulty" />
				<VictoryBar data={props.data} x="project" y="enjoyability" />
			</VictoryGroup>
		</VictoryChart>
	);
};

export default AllStudentsGraph;
